import React from 'react'

export const Header = ({setSearch, value, setValue}) => {

   const inputenter = document.getElementById('inputenter')
inputenter.addEventListener('keyup', (e)=>{
  e.preventDefault()
  if(inputenter.focus && e.key === 13){
    console.log('enter')
    setSearch(value)
  }
},false)
    return (
       <header className="head">
            <h3>Search Hero or Villain:</h3>
            <label >
               <input type='text' id='inputenter' value={value} onChange={(e)=>setValue(e.target.value)}/>
               <input type='button' value='Search' onClick={()=>setSearch(value)} disabled={value===''?true:false}/>
           </label>
        </header>
    )
}
