import React from 'react'
import {ReactComponent as icon} from './search.svg'
export const Header = ({setSearch, value, setValue}) => {


    return (
       <header className="head">
            <h3>Search Hero or Villain:</h3>
            <label >
               <input type='text' value={value} onChange={(e)=>setValue(e.target.value)} placeholder="your text"/>
               <button className='btn'  onClick={()=>setSearch(value)} disabled={value===''?true:false}><img src={icon}/></button>
           </label>
        </header>
    )
}
